import './ProjectDownload.css';
import { useLocation } from 'react-router-dom';
import { useAuth0 } from "@auth0/auth0-react";
import React, { useState } from 'react';
 import { checkProjectDownloadEligibilty } from '../../Service/api';

function ProjectDownload() {
  const { loginWithPopup, isLoading, logout, isAuthenticated, user } = useAuth0();
  const { search } = useLocation();
  const queryParams = new URLSearchParams(search);
  const projectID = queryParams.get('projectID');
  const projectString = queryParams.get('documents');
  const documents = JSON.parse(decodeURIComponent(projectString));
  const [eligible, setEligible] = useState(null);
  React.useEffect( () => {
    if (!isAuthenticated) 
    {
      setEligible(false);
    }
    else
    {
      initiateCheck();
    }
  }, []);
  const initiateCheck = async () => {
    const response = await checkProjectDownloadEligibilty(user.email,projectID);
    setEligible(response.eligible);
  }

  if(eligible)
  {
    return (
      <>
        <h1>Hello, you're in ProjectDownload</h1>
        {documents.map((document, index) => (
          <div key={index}>
            <h2>{index}</h2>
            <p>{document.key}</p>
            <a href={document.value}><p>{document.value}</p></a>
            <br />
          </div>
        ))}
      </>
    );
  }
  else if(eligible==null)
  {
    return (
      <>
        <h1>Loading...</h1>
      </>
    );
  }
  else
  {
    return (
      <>
        <h1>You're Not Eligible !!</h1>
      </>
    );
  }
}

export default ProjectDownload;
