import React, { useState, useEffect, useRef } from 'react';
import './AddProject.css';
import { uploadFile, uploadDocument, connectProjectToUser } from '../../Service/api';
import { useAuth0 } from "@auth0/auth0-react";
import { useNavigate } from 'react-router-dom';
import Header from '../../Components/Header/Header';
import { useData } from '../../DataContext';

const AddProject = () => {
  const [textInputs, setTextInputs] = useState({
    textInput1: '',
    textInput2: '',
  });
  const [fileInputs, setFileInputs] = useState([]);
  const [moreField, setmoreField] = useState(['']);
  const [stringFileMap, setStringFileMap] = useState(new Map());
  const [coverPhoto, setcoverPhoto] = useState('');
  const { isAuthenticated , user } = useAuth0();
  const navigate = useNavigate();
  const [domain, setDomain] = useState('');
  const [selectedTags, setSelectedTags] = useState(['All']);
  const [tag, setTag] = useState('');
  const [price, setPrice] = useState(0);
  const { dropdownData } = useData();
  const [domainList, setDomainList] = useState([]);
  const [topicList, setTopicList] = useState([]);
  const [domainValue, setdomainValue] = useState('');
  React.useEffect(() => {
      if (!isAuthenticated) {
        navigate('/', { replace: true });
      }
  }, [isAuthenticated]);
  useEffect(()=>{
    setDomainsArray();
  },[]);
  useEffect(()=>{
    setTopicsArray();
  },[domain]);

  const setDomainsArray = async () => {
    const dynamicOptions = [];
    for (const key in dropdownData) {
      dynamicOptions.push(key);
    }
    setDomainList(dynamicOptions);
  };
  const setTopicsArray = async () => {
    var c=0;
    for (const key in dropdownData) {
      if(key==domain)
      {
        c=1;
        setTopicList(dropdownData[key]);
        break;
      }
    }
    if(c==0)
    setTopicList([]);
  };
  const handlePriceChange = (e) => {
    setPrice(e.target.value);
  };
  const handleTagUnselect = (e) => {
    const selectedTag = e.currentTarget.dataset.value;
    // Add the selected tag into topicList
    setTopicList([...topicList, selectedTag]);
    // Remove the selected tag from the selectedTags array
    const updatedSelectedTags = selectedTags.filter((tag) => tag !== selectedTag);
    setSelectedTags(updatedSelectedTags);
  };
  const handleAddTag = (e) => {
    setSelectedTags([...selectedTags,e.target.value]);
    const updatedTopicList = topicList.filter(tag => tag !== e.target.value);
    setTopicList(updatedTopicList);
  };
  const handleDomainChange = (e) => {
    setDomain(e.target.value);
  };
  const handleFileChange = (e, key) => {
    const file = e.target.files[0];
    setStringFileMap((prevMap) => new Map(prevMap.set(key, file)));
  };
  const handleTextChange = (e) => {
    setTextInputs({ ...textInputs, [e.target.name]: e.target.value });
  };
  const handleCoverPhotoChange = (e) => {
    setcoverPhoto(e.target.files[0]);
  };
  const handleTextmoreField = (e) => {
    setmoreField(e.target.value);
  };
  const deleteField = (input) => {
    stringFileMap.delete(input);
    setFileInputs(fileInputs.filter((item) => item !== input));
  };
  const addFileInput = () => {
    if(fileInputs.includes(moreField))
    return;
    setmoreField('');
    const newFileInputs = [...fileInputs, moreField];
    setFileInputs(newFileInputs);
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    // Redirect to Success Page
    navigate('/upload-success');

    // Create a javascript object to store all data
    const projectDocuments = [];

    // Storing necessary details in JS Object
    const projectData = {};
    projectData['title'] = textInputs.textInput1;
    projectData['description'] = textInputs.textInput2;
    projectData['rating'] = 0;
    projectData['owner'] = user.email;
    projectData['price'] = price;
    
    // Add Domain
    const domain="Computer Science";
    projectData['domain'] = domain;
    // Add Topic tags
    const tags=['All', 'App Development'];
    projectData['tags'] = tags;

    // Storing Cover Photo in JS Object if provided
    if (coverPhoto) {
        const data = new FormData();
        data.append("name", coverPhoto.name);
        data.append("file", coverPhoto);
        const coverPhotoLink = await uploadFile(data);
        projectData['coverphoto'] = coverPhotoLink.path;
    } else {
        projectData['coverphoto'] = "";
    }

    // Storing the document links in array
    for (const key of stringFileMap.keys()) {
        const file = stringFileMap.get(key);
        if (file) {
            const data = new FormData();
            data.append("name", file.name);
            data.append("file", file);
            const response = await uploadFile(data);

            // Adding Document-DocumentLink as key-value pairs
            projectDocuments.push({ key, value: response.path });
        }
    }
    // Added documents object into our main object.
    projectData['documents'] = projectDocuments;
    
    // Uploaded Project to DB & recieved the documentID
    const projectDocumentData = await uploadDocument(projectData);

    // Storing the documentID in projects list of User.
    const documentIDData = {};
    documentIDData['id']=projectDocumentData.id;
    documentIDData['email']=user.email;
    await connectProjectToUser(documentIDData);
  };

  return (
    <>
      <Header/>
      <div>
      <form onSubmit={handleSubmit}>
      <label>Title :<input type="text" name="textInput1" value={textInputs.textInput1} onChange={handleTextChange} required /></label>
      <br/>
      <label>Description :<input type="text" name="textInput2" value={textInputs.textInput2} onChange={handleTextChange} required /></label>
      <br/>
      <label>UPLOAD COVER PHOTO (optional) : <input type="file" name={`coverphoto`} onChange={(e) => handleCoverPhotoChange(e)} /></label>
      <div id="fileInputsContainer">
        {fileInputs.map((input, index) => (
          <div key={input}>
            <label>
              {`${input}`}
              <input type="file" name={`fileInput[${index}]`} onChange={(e) => handleFileChange(e, input)} required />
            </label>
            <button className='form-button' type="button"  onClick={(e) => deleteField(input)}>
                Delete field
            </button>
            <br/>
          </div>
        ))}
      </div>

      <label>
        Add File Name :
        <input type="text" name="textInput2" value={moreField} onChange={handleTextmoreField} />
      </label>
      <button className='form-button' type="button" onClick={addFileInput}>
        Add File Input
      </button>
      <br/>

      <label htmlFor="dropdown">Select an option:</label>
      <select id="dropdown" value={domain} onChange={handleDomainChange}>
        <option value="">Select...</option>
        {domainList.map((domain1)=>(
          <option value={domain1}>{domain1}</option>
        ))}
      </select>
      <br/>
      <p>Selected Domain: {domain}</p>
      
      <br/>
      <br/>
      <label htmlFor="dropdown">Select an option:</label>
      <select id="dropdown" value={tag} onChange={handleAddTag}>
        <option value="">Select...</option>
        {topicList.map((domain1)=>(
          <option value={domain1}>{domain1}</option>
        ))}
      </select>
      
      <br/>
      <div className='tag-list'>
        {selectedTags.map((tag ? : tag)=>(
          <p className='selected-tag' onClick={handleTagUnselect} data-value={tag}>{tag}</p>
      ))}
      </div>
      
      <br/>
      <label>
        Price :
        <input type="text" name="Price" value={price} onChange={handlePriceChange} />
      </label>
      <br/>
      <br/>

      <button type="submit">Submit</button>
      <br/>
    </form>
      </div>
    </>
    
  );
};

export default AddProject;
