import React, {useEffect} from 'react';
import Header from '../../Components/Header/Header';
import './PaymentSuccess.css';
import { useLocation, useNavigate } from 'react-router-dom';
import { getBalance, updateEarn } from '../../Service/api';

function PaymentSuccess() {
  const { search } = useLocation();
  const queryParams = new URLSearchParams(search);
  const ORDERID=queryParams.get('orderId');
  const RESPMSG=queryParams.get('message');
  const SELLER=queryParams.get('seller');
  const BUYER=queryParams.get('buyer');
  const TXN_AMOUNT=queryParams.get('amount');
  const PROJECT_ID=queryParams.get('projectid');
  React.useEffect(()=>{
    callSuccessDataSave();
  }, []);
  const callSuccessDataSave = async () => {
    const balanceResponse = await getBalance({email: SELLER});
    const finalEarn = calculateFinalEarn(balanceResponse.earned);
    const earnUpdationResponse = await updateEarn(finalEarn);
    // call an API, to set these success data in DB
  }
  function calculateFinalEarn(currentEarn) {
    var c = TXN_AMOUNT * 0.153;
    c = c + currentEarn;
    return c;
}
  return (
    <>
        <Header/>
      <h1>Hello , you're PaymentSuccess</h1> 
    </>
  );
}

export default PaymentSuccess;
