import React, { useState, useEffect } from 'react';
import './Home.css';
import { useNavigate } from 'react-router-dom';
import Header from '../../Components/Header/Header';
import { useAuth0 } from "@auth0/auth0-react";
import { fetchDropDownDataDB } from '../../Service/api';

function Home() {
  const { isAuthenticated, user } = useAuth0();
  const navigate = useNavigate();

  const [domainValue, setdomainValue] = useState('');
  const [TopicValue, setTopicValue] = useState('');
  const [Options2, setOptions2] = useState([]);
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [domains, setDomains] = useState([]);
  const [topics, setTopics] = useState({});

  React.useEffect(()=>{
    fetchDropDownData();
  },[]);

  const fetchDropDownData = async () => {
    const response = await fetchDropDownDataDB();
    // console.log(response.data.domain);
    await convertToObject1AndObject2(response.data.domain);
    console.log(domains);
  };

  const convertToObject1AndObject2 = async (inputObject) => {
    const dynamicOptions1 = [];
  
    for (const key in inputObject) {
      
      const element = inputObject[key];
      dynamicOptions1.push({ value: key, label: element.name });

    }
  
    setDomains(dynamicOptions1);
  };

  // useEffect(() => {
  //   // Update options of Dropdown 2 based on the selected value of Dropdown 1
  //   setOptions2(topics[domainValue] || []);
  //   // Reset selected value of Dropdown 2
  //   setTopicValue('');
  // }, [domainValue]);

  const handleOption1Change = (event) => {
    setdomainValue(event.target.value);
  };

  const handleOption2Change = (event) => {
    setTopicValue(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setFormSubmitted(true);
    if(domainValue=='')
    return;
    // Perform actions with selected options, e.g., submit to a server
    setFormSubmitted(false);
    // navigate(`/project-list?domain=${domainValue}&topic=${TopicValue === '' ? 'All' : TopicValue}`);
    navigate(`/project-list?domain=${getDomain(domainValue)}&topic=${getTopic(TopicValue)}`);
  };
  function getDomain(selectedDomain){
    switch(selectedDomain)
    {
      case 'computerscience' :
        return 'Computer Science';
      case 'mechanical' :
        return 'Mechanical';
      case 'civil' :
        return 'Civil';
    }
  };
  function getTopic(selectedTopic){
    switch(selectedTopic)
    {
      case 'appdev' :
        return 'App Development';
      case 'machinelearning' :
        return 'Machine Learning';
      case 'blockchain' :
        return 'Blockchain';
      default:
        return 'All';
    }
  }

  return (
    <>
      <Header/>
      <div className='body-div'>
        <div>
          <h1>Welcome to Project Vault</h1>
          <p>Explore the perfect projects for your academic requirements, with all source codes, reports, included</p>
        </div>
        
        { domains && topics && <form onSubmit={handleSubmit}>
          <div className='dropdown'>
            {/* <select className='dropdown-box' value={domainValue} onChange={handleOption1Change}> */}
            <select className={`dropdown-box${formSubmitted && domainValue === '' ? '-error' : ''}`} value={domainValue} onChange={handleOption1Change}>
              <option value=''>Select Domain</option>
              {domains.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>

          <div className='dropdown'>
            <select className='dropdown-box' value={TopicValue} onChange={handleOption2Change}>
              <option value=''>Select Topic</option>
              {Options2.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>

          <button type='submit'>Search Projects</button>
        </form>}
      </div>
    </>
  );
}

export default Home;
