import React, { useState } from 'react';import './Header.css';
import { useNavigate } from 'react-router-dom';
import { useAuth0 } from "@auth0/auth0-react";
import Modal from 'react-modal';
import { useData } from '../../DataContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUpload } from '@fortawesome/free-solid-svg-icons';
Modal.setAppElement('#root');


function Header() {
  const imagePath = process.env.PUBLIC_URL + '/images/logo-t.png';
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { isAuthenticated, user, logout  } = useAuth0();
  const { setLastVisitedPage } = useData();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };
  const handleAddProject = () => {
    navigate(`/addproject`);
  };
  const handleCallLoginPage = () => {
    setLastVisitedPage(window.location.pathname + window.location.search);
    navigate(`/login`, {replace: true});
  };
  const handleProfile = () => {
    setIsMenuOpen(false);
    navigate(`/profile`);
  };
  const handleLogOut = () => {
    setIsMenuOpen(false); 
    logout();
  };
  const handleLogoClick = () => {
    navigate(`/`);
  }
  return (
    <>
      <header className='header'>
        <div className='header-nav'>
          { isAuthenticated  &&
            <div className='upload-div' onClick={handleAddProject} >
              <FontAwesomeIcon className='icon' icon={faUpload} />
              <p className='text'>UPLOAD</p>
            </div>  
          }
        </div>
        <div className='header-logo' onClick={handleLogoClick}>
          <img src={imagePath} />
        </div>
        <div className='header-nav'>
          { !isAuthenticated  
            ? <a onClick={handleCallLoginPage}>
                <div>Login</div>
              </a> 
            : <div onClick={toggleMenu} >
                <img src={user.picture} />
              </div>  
          }
        </div>
        <Modal
        isOpen={isMenuOpen}
        onRequestClose={toggleMenu}
        contentLabel="Example Modal"
        className="Modal"
        overlayClassName="OverlayPopup"
        >
          <div className="menu">
            <button className='menu-button-profile' onClick={handleProfile}>My Profile</button>
            <button className='menu-button-logout' onClick={handleLogOut}>Logout</button>
          </div>
        </Modal>
      </header>
      
    </>
    
  );
}

export default Header;
