import './EditProject.css';
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useAuth0 } from "@auth0/auth0-react";
import { checkProjectOwnership, fetchProjectfromDB } from '../../Service/api';
import { ThreeDots    } from 'react-loader-spinner';
  

function EditProject() {
    const { isAuthenticated, user } = useAuth0();
    const navigate = useNavigate();
    const { search } = useLocation();
    const queryParams = new URLSearchParams(search);
    const projectID = queryParams.get('projectID');
    const [loading , setLoading] = useState(true);
    const [project , setProject] = useState(null);
    React.useEffect( () => {
        checkProjectOwnershipFunction();
    }, [isAuthenticated]);
    React.useEffect( () => {
        if(isAuthenticated && projectID)
        fetchProjectData();
    }, []);
    const checkProjectOwnershipFunction = async () => {
        if (!isAuthenticated) {
            navigate('/', { replace: true });
        }
        else
        {
            const res = await checkProjectOwnership(user.email, projectID);
            if(res.owner==false)
            {
                navigate('/', { replace: true });
            }
        }
    };
    const fetchProjectData = async () => {
        try {
            const res = await fetchProjectfromDB(projectID);
            setProject(res.project);
            setLoading(false);
        } catch (error) {
            setLoading(false);
        }
    };

  return (
    <>
        { loading &&
            <div className='loader-message'>
                <ThreeDots 
                    color="#000000"
                    height={50}
                    width={50}
                />
            </div>
        }
        { !loading && project &&
            <p>Edit Project ({projectID})</p>
        }
    </>
  );
}

export default EditProject;
