import './ProjectList.css';
import { useLocation } from 'react-router-dom';
import Header from '../../Components/Header/Header';

function ProjectList() {
  const { search } = useLocation();
  const queryParams = new URLSearchParams(search);
  const selectedOption1 = queryParams.get('domain');
  const selectedOption2 = queryParams.get('topic');

  const projectList = [
    { 
      id: '',
      title: 'Title',
      description: 'Description',
      avdocs: ['Source Code', 'Reports'],
      topic: 'Topic',
      rating: '5',
    },
  ];

  const handleProjectClick = (projectId) => {
    // Perform actions with the clicked project ID, e.g., log or send to a server
    console.log('Clicked Project ID:', projectId);

    // Set the selected project ID in the state
    // Assuming you have a state variable for the selected project ID
    // Replace 'selectedProjectId' with the actual state variable you use
    // setSelectedProjectId(projectId);

    // Navigate to the next page with the project ID as a query parameter
    navigate(`/project-details?projectId=${projectId}`);
  };

  return (
    <>
      <Header/>
      <div className='spacer'></div>
      <div className='heading'>
        <h2>Here are your search results</h2>
        <div className='fields'>
          <div className='field-display'>
            <p>{selectedOption1}</p>
          </div>
          <div className='field-display'>
            <p>{selectedOption2}</p>
          </div>
        </div>
      </div>

      {projectList.map((project) => (
        <div className='project-box' onClick={() => openProject(project.id)}>
        <div className='LHS'>
          <h3>{project.title}</h3>
          <p>{project.description}</p>
          <div className='av-docs'>
            {project.avdocs.map((docs) => (
              <div>
                <div className='doc'><p>{docs}</p></div>
              </div>
            ))}
          </div>
        </div>
        <div className='RHS'>
          <div className='topic'>
            <p>{project.topic}</p>
          </div>
          <div className='rating'>
            <p>{project.rating}/5</p>
          </div>
        </div>
      </div>
      ))}

    </>
  );
}

export default ProjectList;
