import React, { useState } from 'react';
import './ProjectUpload.css';
import { uploadFile, uploadDocument } from '../../Service/api';
import { useAuth0 } from "@auth0/auth0-react";
import { useNavigate } from 'react-router-dom';

const ProjectUpload = () => {
  const [textInputs, setTextInputs] = useState({
    textInput1: '',
    textInput2: '',
  });
  const [fileInputs, setFileInputs] = useState([]);
  const [moreField, setmoreField] = useState(['']);
  const [stringFileMap, setStringFileMap] = useState(new Map());
  const [coverPhoto, setcoverPhoto] = useState('');
  const [result, setResult] = useState('');
  const { isAuthenticated } = useAuth0();
  const navigate = useNavigate();



  const handleFileChange = (e, key) => {
    const file = e.target.files[0];
    setStringFileMap((prevMap) => new Map(prevMap.set(key, file)));
  };

  const handleTextChange = (e) => {
    setTextInputs({ ...textInputs, [e.target.name]: e.target.value });
  };

  const handleCoverPhotoChange = (e) => {
    setcoverPhoto(e.target.files[0]);
  };

  const handleTextmoreField = (e) => {
    setmoreField(e.target.value);
  };

  const deleteField = (input) => {
    stringFileMap.delete(input);
    setFileInputs(fileInputs.filter((item) => item !== input));
  };

  const addFileInput = () => {
    if(fileInputs.includes(moreField))
    return;
    setmoreField('');
    const newFileInputs = [...fileInputs, moreField];
    setFileInputs(newFileInputs);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create a javascript object to store all data
    const projectDocuments = [];

    // Storing necessary details in JS Object
    const projectData = {};
    projectData['title'] = textInputs.textInput1;
    projectData['description'] = textInputs.textInput2;
    projectData['rating'] = 0;

    // Storing Cover Photo in JS Object if provided
    if (coverPhoto) {
        const data = new FormData();
        data.append("name", coverPhoto.name);
        data.append("file", coverPhoto);
        const coverPhotoLink = await uploadFile(data);
        projectData['coverphoto'] = coverPhotoLink.path;
    } else {
        projectData['coverphoto'] = "";
    }

    // Storing the document links in array
    for (const key of stringFileMap.keys()) {
        const file = stringFileMap.get(key);
        if (file) {
            const data = new FormData();
            data.append("name", file.name);
            data.append("file", file);
            const response = await uploadFile(data);

            // Adding Document-DocumentLink as key-value pairs
            projectDocuments.push({ key, value: response.path });
        }
    }

    projectData['dynamicDocuments'] = projectDocuments;

    const projectDocumentData = await uploadDocument(projectData);

    // Write code to implement the storing of all data in DB
    // - we will upload a document in collection(projects)
    // - fetch the document ID in response
    // - store the document ID in USER Collection's project's List.
    console.log(projectDocumentData);
    try {setResult(projectDocumentData.id);} 
    catch (e) {setResult(e.message);}
};


  return (
    <form onSubmit={handleSubmit}>
      <label>
        Title :
        <input type="text" name="textInput1" value={textInputs.textInput1} onChange={handleTextChange} required />
      </label>
      <br/>

      <label>
        Description :
        <input type="text" name="textInput2" value={textInputs.textInput2} onChange={handleTextChange} required />
      </label>
      <br/>

      <label>
        UPLOAD COVER PHOTO (optional) : 
        <input type="file" name={`coverphoto`} onChange={(e) => handleCoverPhotoChange(e)} />
      </label>

      <div id="fileInputsContainer">
        {fileInputs.map((input, index) => (
          <div key={input}>
            <label>
              {`${input}`}
              <input type="file" name={`fileInput[${index}]`} onChange={(e) => handleFileChange(e, input)} required />
            </label>
            <button type="button"  onClick={(e) => deleteField(input)}>
                Delete field
            </button>
            <br/>
          </div>
        ))}
      </div>

      <label>
        Add File Name :
        <input type="text" name="textInput2" value={moreField} onChange={handleTextmoreField} />
      </label>
      <button type="button" onClick={addFileInput}>
        Add File Input
      </button>
      <br/>

      <button type="submit">Submit</button>
      <br/>
      <br/>
      <br/>
      <a href={result} target='_blank'>{result}</a> 
    </form>
  );
};

export default ProjectUpload;
