import './Rating.css';

function Rating(props) {
  return (
    <>
        <div className="product-rating">
          <p>Rating: </p>
            <div className="star-rating">
              <Star rating="0.9 " index="1"/>
              <Star rating="0.9 " index="2"/>
              <Star rating="0.9 " index="3"/>
              <Star rating="0.9 " index="4"/>
              <Star rating="0.9 " index="5"/>
              {/* <Star rating={props.rating} index="1"/>
              <Star rating={props.rating} index="2"/>
              <Star rating={props.rating} index="3"/>
              <Star rating={props.rating} index="4"/>
              <Star rating={props.rating} index="5"/> */}
            </div>
          <p>{props.rating}/5</p>
        </div>
    </>
    
  );
}
function Star(props) {
  if(props.rating<=props.index-1)
  {
    return (
      <span className="no-star" >&#9733;</span>
    );
  }
  else if(props.rating>props.index)
  {
    return (
      <span className="full-star" >&#9733;</span>
    );
  }
  else
  {
    return (
      <span className="half-star" >&#9733;</span>
    );
  }
}

export default Rating;
