import React, { useState } from 'react';
import './Project.css';
import { useLocation, useNavigate } from 'react-router-dom';
import Header from '../../Components/Header/Header';
import Rating from '../../Components/Rating/Rating';
import { useAuth0 } from "@auth0/auth0-react";
import { checkProjectDownloadEligibilty } from '../../Service/api';

function Project() {
  const imagePath = process.env.PUBLIC_URL + '/images/coverImage.png';
  const { isAuthenticated, user } = useAuth0();
  const { search } = useLocation();
  const navigate = useNavigate();
  const queryParams = new URLSearchParams(search);
  const projectString = queryParams.get('data');
  const project = JSON.parse(decodeURIComponent(projectString));
  const [eligible, setEligible] = useState(null);

  React.useEffect( () => {
    if (isAuthenticated)
    {
      initiateCheck();
    }
  }, []);
  const initiateCheck = async () => {
    const response = await checkProjectDownloadEligibilty(user.email,project._id);
    setEligible(response.eligible);
  }
  const handlePurchase = () => {
    // Save the downloaded project's ID in DB > USER > downloads[]
    alert('Download option not available yet!');
  };
  const handleDownload = () => {
    // Navigate to project download page
    const jsonData = project.documents;
    const jsonDataString = encodeURIComponent(JSON.stringify(jsonData));
    navigate(`/project-download?documents=${jsonDataString}&projectID=${project._id}`);
  };
  const handleBuy = () => {
    // Navigate to project download page
    if(isAuthenticated)
    {
      // Implement payment logic
      setTimeout(() => {
        getData({ amount: 100, email: "codesense24@gmail.com" }).then((response) => {
          var information = {
            // for production only
            action: "https://securegw.paytm.in/order/process",
            params: response,
          };
          console.log(response)
          post(information);
        });
      }, 1500);
      alert('Purchase option not available yet  !');
    }
    else
    alert('Login to Buy !');
  };
  function isDate(val) {
    // Cross realm comptatible
    return Object.prototype.toString.call(val) === "[object Date]";
  }

  function isObj(val) {
    return typeof val === "object";
  }

  function stringifyValue(val) {
    if (isObj(val) && !isDate(val)) {
      return JSON.stringify(val);
    } else {
      return val;
    }
  }

  function buildForm({ action, params }) {
    const form = document.createElement("form");
    form.setAttribute("method", "post");
    form.setAttribute("action", action);

    Object.keys(params)?.forEach((key) => {
      const input = document.createElement("input");
      input.setAttribute("type", "hidden");
      input.setAttribute("name", key);
      input.setAttribute("value", stringifyValue(params[key]));
      form.appendChild(input);
    });

    return form
  }

  function post(details) {
    const form = buildForm(details);
    document.body.appendChild(form);
    form.submit();
    form.remove();
  }

  const getData = (data) => {
    return fetch(`http://localhost:8000/api/payment`, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .catch((err) => console.log(err));
  };
  
  return (
    <>
      <Header/>
      <div className='body-project'>
        <div className='LHS'>
          <img src={project.coverphoto} />
          <Rating rating={project.rating} />
        </div>
        <div className='RHS'>
          <h2>{project.title}</h2>
          <p>{project.description}</p>
          <p><b>You will get :</b></p>
          <div className='av-docs'>
            {project.documents.map((docs,index) => (
              <div key={index}>
                <div className='doc'><p>{docs.key}</p></div>
              </div>
            ))}
          </div>
          <p><strike>â‚¹300</strike> Free</p>
          { eligible 
            ? <button type="button" onClick={handleDownload}>Download</button>
            : <button type="button" onClick={handleBuy}>Buy</button>
          }
        </div>
      </div>
    </>
  );
}

export default Project;
