import React, { useState, useEffect } from 'react';
import Header from '../../Components/Header/Header';
import './Login.css';
import { useAuth0 } from "@auth0/auth0-react";
import { useNavigate } from 'react-router-dom';
import { addUserSignInDataToDB, checkUserExists } from '../../Service/api';

function Login() {
    const { loginWithPopup, isLoading, logout, isAuthenticated, user } = useAuth0();
    const navigate = useNavigate();

    React.useEffect( () => {
        if (isAuthenticated) {
            handleAddSignInDataToDB();
            navigate('/', { replace: true });
        }
    }, [isAuthenticated]);

    async function handleAddSignInDataToDB(){
        // check user exists or not in MongoDB
        const data={};
        data['email']= user.email ;
        const userState = await checkUserExists(data);
            if(!userState['exists'])
            await addUserSignInDataToDB(data);
            else
            console.log("User Existed ...");
    }

    console.log(user);
    return (
        <>
        <Header/>
        <h1>Hello {(isAuthenticated) ? user.name : ""}, you're Login</h1> 
        {
            (isAuthenticated) ? 
            <button onClick={() => logout({ logoutParams: { returnTo: window.location.origin } })}>
                Log Out
            </button>
            : 
            <button onClick={() => loginWithPopup()}>Log In</button> 
        }
        </>
    );
}

export default Login;
