import React, { useState } from 'react';import './Header.css';
import { useNavigate } from 'react-router-dom';
import { useAuth0 } from "@auth0/auth0-react";
import { useData } from '../../DataContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faUpload } from '@fortawesome/free-solid-svg-icons';


function Header() {
  const imagePath = process.env.PUBLIC_URL + '/images/logo-t.png';
  const navigate = useNavigate();
  const { isAuthenticated, user } = useAuth0();
  const { setLastVisitedPage } = useData();

  const handleAddProject = () => {
    navigate(`/addproject`);
  };
  const handleCallLoginPage = () => {
    setLastVisitedPage(window.location.pathname + window.location.search);
    navigate(`/login`, {replace: true});
  };
  const handleProfile = () => {
    navigate(`/profile`);
  };
  const handleLogoClick = () => {
    navigate(`/`);
  }
  return (
    <>
      <header className='header'>
        <div className='header-nav'>
          { isAuthenticated && window.location.pathname!="/addproject"  &&
            <div className='upload-div' onClick={handleAddProject} >
              <FontAwesomeIcon className='icon' icon={faUpload} />
              <p className='text'>UPLOAD</p>
            </div>  
          }
        </div>
        <div className='header-logo' onClick={handleLogoClick}>
          <img src={imagePath} />
        </div> 
        <div className='header-nav'>
          { !isAuthenticated  && window.location.pathname!="/login" &&
            <div className='upload-div' onClick={handleCallLoginPage} >
                <FontAwesomeIcon className='icon' icon={faUser} />
                <p className='text'>LOGIN</p> 
              </div>  
          }
          { window.location.pathname!="/profile"  && isAuthenticated  &&
            <div className='upload-div' onClick={handleProfile} >
              { user.picture!=null 
                ? <img className='icon-img' src={user.picture} />
                : <FontAwesomeIcon className='icon' icon={faUser} />
              }
              <p className='text'>PROFILE</p>
            </div> 
          }
        </div>
      </header>
      
    </>
    
  );
}

export default Header;
