import React, { useState } from 'react';
import './ProjectUpload.css';

const ProjectUpload = () => {
  const [textInputs, setTextInputs] = useState({
    textInput1: '',
    textInput2: '',
  });

  const [fileInputs, setFileInputs] = useState(['fileInput1']);
  const [moreField, setmoreField] = useState(['']);

  const handleTextChange = (e) => {
    setTextInputs({ ...textInputs, [e.target.name]: e.target.value });
  };

  const handleTextmoreField = (e) => {
    setmoreField(e.target.value);
  };

  const handleFileChange = (e, index) => {
    // Handle file changes if needed
  };

  const addFileInput = () => {
    const newFileInputs = [...fileInputs, `fileInput${fileInputs.length + 1}`];
    setFileInputs(newFileInputs);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log('Text Inputs:', textInputs);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Text Input 1:
        <input type="text" name="textInput1" value={textInputs.textInput1} onChange={handleTextChange} required />
      </label>

      <label>
        Text Input 2:
        <input type="text" name="textInput2" value={textInputs.textInput2} onChange={handleTextChange} required />
      </label>

      <div id="fileInputsContainer">
        {fileInputs.map((input, index) => (
          <div key={input}>
            <label>
              {`File Input ${index + 1}:`}
              <input type="file" name={`fileInput[${index}]`} onChange={(e) => handleFileChange(e, index)} required />
            </label>
          </div>
        ))}
      </div>

      <label>
        Add File Name :
        <input type="text" name="textInput2" value={moreField} onChange={handleTextmoreField} />
      </label>
      <button type="button" onClick={addFileInput(moreField)}>
        Add File Input
      </button>

      <button type="submit">Submit</button>
    </form>
  );
};

export default ProjectUpload;
