import React, { useState, useEffect } from 'react';
import './Profile.css';
import { useNavigate } from 'react-router-dom';
import { useAuth0 } from "@auth0/auth0-react";
import { fetchAllProjectsOfUser } from '../../Service/api';

function Profile() {
  const { logout, isAuthenticated, user } = useAuth0();
  const navigate = useNavigate();
  const [projects, setProjects] = useState(null);


  React.useEffect( () => {
    if (!isAuthenticated) {
      navigate('/', { replace: true });
    }
    fetchProjectList();
  }, []);
  console.log(projects);

  const fetchProjectList = async () => {
    const ProjectsResponse = await fetchAllProjectsOfUser(user.email);
    try {
      setProjects(ProjectsResponse.projects.documents);
      for(const id in projects)
      {
        fetchProjectDetails(id);
      }
      for (let i = 0; i < projects.length; i++) {
        fetchProjectDetails(id);
      }
    } catch (error) {}
  };

  const handleLogOut = () => {
    logout();
  };

  if(isAuthenticated)
  {
    return (
      <>
        <div className='user-info-div'>
          <div className='user-image'>
            <img src={user.picture} /> 
          </div>
          <div className='user-info'>
            <p className='info'>{user.name}</p>
            <p className='info'>{user.email}</p>
          </div>
        </div>

        <p>Your Projects</p>
        <div className='projects'>

        </div>
        
        <button className='button-logout' onClick={handleLogOut}>Logout</button>
      </>
    );
  }
  else
  {
    return (
      <>
        <h1>Profile does'nt exists</h1> 
      </>
    );
  }
  
}

export default Profile;
