import React, { useState } from 'react';import './Header.css';
import { useNavigate } from 'react-router-dom';
import { useAuth0 } from "@auth0/auth0-react";
import { useData } from '../../DataContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Modal from 'react-modal';
import { faBars, faPlus } from '@fortawesome/free-solid-svg-icons';


function Header() {
  const imagePath = process.env.PUBLIC_URL + '/images/logo-t.png';
  const navigate = useNavigate();
  const { isAuthenticated, user } = useAuth0();
  const { setLastVisitedPage } = useData();
  const [menuOpen, setMenuOpen] = useState(false);

  const handleAddProject = () => {
    navigate(`/addproject`);
  };
  const handleCallLoginPage = () => {
    setLastVisitedPage(window.location.pathname + window.location.search);
    navigate(`/login`, {replace: true});
  };
  const handleProfile = () => {
    navigate(`/profile`);
  };
  const customStyles = {
    overlay: {
      backgroundColor: 'rgba(0, 0, 0, 0)', // Make overlay transparent
    },
    content: {
      top: '50%', // Center vertically
      left: '50%', // Center horizontally
      right: 'auto',
      bottom: 'auto',
      transform: 'translate(-50%, -50%)', // Center content
      border: 'none', // Remove border
      boxShadow: '0 4px 8px rgba(0,0,0,0.1)', // Add shadow
      padding: '20px',
      maxWidth: '400px', // Limit width if needed
      background: '#fff', // White background
    },
  };
  const handleLogoClick = () => {
    navigate(`/`);
  }
  const handleManuIconClick = () => {
    setMenuOpen(!menuOpen);
  };
  return (
    <>
      <header className='header'>
        {/* <div className='header-nav'>
          { isAuthenticated && window.location.pathname!="/addproject"  &&
            <div className='upload-div' onClick={handleAddProject} >
              <FontAwesomeIcon className='icon' icon={faUpload} />
              <p className='text'>UPLOAD</p>
            </div>  
          }
        </div> */}
        <div className='header-nav'></div> {/* extra <div> just to center the logo */}
        <div className='header-logo' onClick={handleLogoClick}>
          <img src={imagePath} />
        </div> 
        <div className='header-nav'>
            <div className='menu-div' onClick={handleManuIconClick} >
              <FontAwesomeIcon className='icon' icon={faBars} />
            </div> 
        </div>
        <Modal
            className='menu-modal'
            isOpen={menuOpen}
            onRequestClose={handleManuIconClick}
            overlayClassName="menu-overlay"
            style={customStyles}
          >
          <div className='menu-outer'>
            <div className='menu-box'>
              <FontAwesomeIcon className='menu-box-icon' icon={faPlus} />
              <h3 className='menu-box-text'>Upload New Project</h3>
            </div>
          </div>
        </Modal>
        {/* <div className='header-nav'>
          { !isAuthenticated  && window.location.pathname!="/login" &&
            <div className='upload-div' onClick={handleCallLoginPage} >
                <FontAwesomeIcon className='icon' icon={faUser} />
                <p className='text'>LOGIN</p> 
              </div>  
          }
          { window.location.pathname!="/profile"  && isAuthenticated  &&
            <div className='upload-div' onClick={handleProfile} >
              { user.picture!=null 
                ? <img className='icon-img' src={user.picture} />
                : <FontAwesomeIcon className='icon' icon={faUser} />
              }
              <p className='text'>PROFILE</p>
            </div> 
          }
        </div> */}
        
      </header>
      
    </>
    
  );
}

export default Header;
