import React, { useState, useEffect, useRef } from 'react';
import './ProjectList.css';
import { useLocation } from 'react-router-dom';
import Header from '../../Components/Header/Header';
import { useNavigate } from 'react-router-dom';
import { fetchProjectList } from '../../Service/api';
import { useData } from '../../DataContext';

function ProjectList() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const queryParams = new URLSearchParams(search);
  const [selectedDomain, setselectedDomain] = useState(queryParams.get('domain'));
  const [selectedTopic, setselectedTopic] = useState(queryParams.get('topic'));
  const { dropdownData } = useData();
  const [projectList, setProjectList] = useState([]);
  const [loader, setLoader] = useState(true);

  const handleCallProjectList = async () => {
    const response = await fetchProjectList(selectedDomain,selectedTopic);
    setProjectList(response.projects);
    setLoader(false);
  };

  const getDomainTopic = () => {
    for (const key1 in dropdownData) {
      if(key1==selectedDomain)
      {
        const element = dropdownData[key1];
        setselectedDomain(element.name);
        const topicsList = element.topic;
        console.log(topicsList);
        for (const key2 in topicsList)
        {
          if(key2==selectedTopic)
          {
            break;
          }
          
        
        }
      }
    }
  };

  useEffect(() => {
    getDomainTopic();
    handleCallProjectList();
  }, []);


  const handleProjectClick = (project) => {
    const jsonData = project;
    // Encode JSON data to a string and pass it as a URL parameter
    const jsonDataString = encodeURIComponent(JSON.stringify(jsonData));
    // Navigate to the next page with the project ID as a query parameter
    navigate(`/project?data=${jsonDataString}`);
  };

  return (
    <>
      <Header/>
      <div className='heading'>
        <h2>Here are your search results</h2>
        <div className='fields'>
          <div className='field-display'>
            <p>{selectedDomain}</p>
          </div>
          <div className='field-display'>
            <p>{selectedTopic}</p>
          </div>
        </div>
      </div>

      { !loader && projectList.length>0 && projectList.map((project) => (
        <div className='project-box' key={project._id} onClick={() => handleProjectClick(project)}>
        <div className='LHS'>
          <h3>{project.title}</h3>
          <p>{project.description}</p>
          <div className='av-docs'>
            {project.documents.map((docs) => (
              <div key={docs.value}>
                <div className='doc'><p>{docs.key}</p></div>
              </div>
            ))}
          </div>
        </div>
        <div className='RHS'>
          <div className='topic'>
            <p>{project.domain}</p>
          </div>
          <div className='rating'>
            <p>{project.rating}/5</p>
          </div>
        </div>
      </div>
      ))}

    </>
  );
}

export default ProjectList;
