import React, { useState, useEffect } from 'react';
import './Home.css';
import { useNavigate } from 'react-router-dom';
import Header from '../../Components/Header/Header';
import { useAuth0 } from "@auth0/auth0-react";
import { fetchDropDownDataDB } from '../../Service/api';
import { useData } from '../../DataContext';

function Home() {
  const { isAuthenticated, user } = useAuth0();
  const navigate = useNavigate();
  const { dropdownData, setDropDownDataFunction } = useData();

  const [domainValue, setdomainValue] = useState('');
  const [TopicValue, setTopicValue] = useState('ALL');
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [domains, setDomains] = useState([]);
  const [topics, setTopics] = useState([]);
  // const [dropDownData, setDropDownData] = useState(null);

  React.useEffect(()=>{
    if(dropdownData==null)
    fetchDropDownData();
    else
    setDomainsArray(dropdownData);
  },[]);

  const fetchDropDownData = async () => {
    const response = await fetchDropDownDataDB();
    await setDomainsArray(response.data.domain);
    setDropDownDataFunction(response.data.domain);
  };

  const setDomainsArray = async (inputObject) => {
    const dynamicOptions = [];
    for (const key in inputObject) {
      const element = inputObject[key];
      dynamicOptions.push({ value: key, label: element.name });
    }
    setDomains(dynamicOptions);
  };

  const setTopicsArray = async () => {
    const inputObject = dropdownData;
    const dynamicOptions = [];
    for (const key in inputObject) {
      if(key==domainValue)
      {
        const element = inputObject[key].topic || {};
        for (const keyy in element) {
          dynamicOptions.push({value: keyy , label: element[keyy]});
        }
        setTopics(dynamicOptions);
        console.log(topics);
        break;
      }
    }
  };

  useEffect(() => {
    setTopicsArray();
    setTopicValue('ALL');
  }, [domainValue]);

  const handleOption1Change = (event) => {
    setdomainValue(event.target.value);
  };

  const handleOption2Change = (event) => {
    setTopicValue(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setFormSubmitted(true);
    if(domainValue=='')
    return;
    // Perform actions with selected options, e.g., submit to a server
    setFormSubmitted(false);
    navigate(`/project-list?domain=${domainValue}&topic=${TopicValue}`);
  };
  return (
    <>
      <Header/>
      <div className='body-div'>
        <div>
          <h1>Welcome to Project Vault</h1>
          <p>Explore the perfect projects for your academic requirements, with all source codes, reports, included</p>
        </div>
        
        { domains && topics && <form onSubmit={handleSubmit}>
          <div className='dropdown'>
            <select className={`dropdown-box${formSubmitted && domainValue === '' ? '-error' : ''}`} value={domainValue} onChange={handleOption1Change}>
              <option value=''>Select Domain</option>
              {domains.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>

          <div className='dropdown'>
            <select className='dropdown-box' value={TopicValue} onChange={handleOption2Change}>
              <option value=''>Select Topic</option>
              {topics.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>

          <button type='submit'>Search Projects</button>
        </form>}
      </div>
    </>
  );
}

export default Home;
