import React, { useState, useEffect } from 'react';
import './Profile.css';
import { useNavigate } from 'react-router-dom';
import { useAuth0 } from "@auth0/auth0-react";
import { fetchAllProjectsOfUser } from '../../Service/api';
import { fetchProjectfromDB } from '../../Service/api';

function Profile() {
  const { logout, isAuthenticated, user } = useAuth0();
  const navigate = useNavigate();
  const [projects, setProjects] = useState(null);
  const [yourProjects, setYourProjects] = useState([]);


  React.useEffect( () => {
    if (!isAuthenticated) {
      navigate('/', { replace: true });
    }
    if (projects==null) 
    {
      fetchProjectList();
    }
  }, []);
  

  const fetchProjectList = async () => {
    const ProjectsResponse = await fetchAllProjectsOfUser(user.email);
    try {
      setProjects(ProjectsResponse.projects.documents);
      fetchProjectDatafromProjects(ProjectsResponse.projects.documents);
    } catch (error) {}
  };
  
  const fetchProjectDatafromProjects = async (projects2) => {
    const ar = [];
      for (let i = 0; i < projects2.length; i++) {
        try {
          const res = await fetchProjectfromDB(projects2[i]);
          ar.push(res.project);
        } catch (e) {
          console.error('Error fetching project:', e);
        }
      }
      setYourProjects(ar);
  };

  


  const handleLogOut = () => {
    logout();
  };

  if(isAuthenticated)
  {
    return (
      <>
        <div className='user-info-div'>
          <div className='user-image'>
            <img src={user.picture} /> 
          </div>
          <div className='user-info'>
            <p className='info'>{user.name}</p>
            <p className='info'>{user.email}</p>
          </div>
        </div>

        
        <div className='projects'>
          <p>Your Projects</p>
          <div className='projects-in'>
            {  yourProjects && yourProjects.length>0 && yourProjects.map((project) => (
              <div key={project._id} className='project' onClick={handleProject}>
                <div className='image'>
                  <img src={project.coverphoto} />
                </div>
                <div className='title'><p>{project.title}</p></div>
              </div>
            ))}
          </div>
          
        </div>
        
        <button className='button-logout' onClick={handleLogOut}>Logout</button>
      </>
    );
  }
  else
  {
    return (
      <>
        <h1>Profile does'nt exists</h1> 
      </>
    );
  }
  
}

export default Profile;
