import React, { useState } from 'react';import './Header.css';
import { useNavigate } from 'react-router-dom';
import { useAuth0 } from "@auth0/auth0-react";
import Modal from 'react-modal';
import { useData } from '../../DataContext';
Modal.setAppElement('#root');

function Header() {
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { isAuthenticated, user, logout  } = useAuth0();
  const { setLastVisitedPage } = useData();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };
  const handleAddProject = () => {
    navigate(`/addproject`);
  };
  const handleCallLoginPage = () => {
    setLastVisitedPage(window.location.pathname + window.location.search);
    navigate(`/login`, {replace: true});
  };
  const handleProfile = () => {
    setIsMenuOpen(!isMenuOpen);
    navigate(`/profile`);
  };
  const handleLogOut = () => {
    logout();
  };
  return (
    <>
      <div className='spacer'></div>
      <header className='header'>
        <div className='header-logo'>
          <h2>Project Vault</h2>
        </div>
        <div className='header-nav-elements'>
          <a href="/">
            <div>Home</div>
          </a>
          <a href="/about">
            <div>About</div>
          </a>
          <a href="/contact">
            <div>Contact</div>
          </a>
          { isAuthenticated && <a onClick={handleAddProject}>
            <div>Upload</div>
          </a>}
          { !isAuthenticated  
            ? <a onClick={handleCallLoginPage}>
                <div>Login</div>
              </a> 
            : <div onClick={toggleMenu} >
                <img src={user.picture} />
              </div>  
          }
        </div>
        <Modal
        isOpen={isMenuOpen}
        onRequestClose={toggleMenu}
        contentLabel="Example Modal"
        className="Modal"
        overlayClassName="OverlayPopup"
        >
          <div className="menu">
            <button className='menu-button-profile' onClick={handleProfile}>My Profile</button>
            <button className='menu-button-logout' onClick={handleLogOut}>Logout</button>
          </div>
        </Modal>
      </header>
      
    </>
    
  );
}

export default Header;
