import React, { useState, useEffect, useRef } from 'react';
import './ProjectList.css';
import { useLocation } from 'react-router-dom';
import Header from '../../Components/Header/Header';
import { useNavigate } from 'react-router-dom';

function ProjectList() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const queryParams = new URLSearchParams(search);
  const selectedDomain = queryParams.get('domain');
  const selectedTopic = queryParams.get('topic');

  const [projectList, setprojectList] = useState(null);

  useEffect(() => {
    // Function to fetch data from the database
    const fetchData = async () => {
      try {   
        // Make an API request to fetch data from the database
        const response = await axios.get('/get-projects-list', {
          params: {
            domain: selectedDomain,
            topic: selectedTopic,
          },
        });
        setprojectList(response.data); // Update state with the fetched data
      } catch (error) {
        console.error('Error fetching data:', error.message);
      }
    };

    // Call the fetchData function when the component mounts
    fetchData();
  }, []); // The empty dependency array means this effect runs once when the component mounts


  // const projectList = [
  //   { 
  //     id: 'P1',
  //     title: 'Title',
  //     description: 'Description',
  //     avdocs: ['Source Code', 'Reports'],
  //     topic: 'Topic',
  //     rating: '5',
  //   },
  // ];

  const handleProjectClick = (projectId) => {
    // Navigate to the next page with the project ID as a query parameter
    navigate(`/project?projectId=${projectId}`);
  };

  return (
    <>
      <Header/>
      <div className='heading'>
        <h2>Here are your search results</h2>
        <div className='fields'>
          <div className='field-display'>
            <p>{se}</p>
          </div>
          <div className='field-display'>
            <p>{selectedOption2}</p>
          </div>
        </div>
      </div>

      {projectList.map((project) => (
        <div className='project-box' onClick={() => handleProjectClick(project.id)}>
        <div className='LHS'>
          <h3>{project.title}</h3>
          <p>{project.description}</p>
          <div className='av-docs'>
            {project.avdocs.map((docs) => (
              <div>
                <div className='doc'><p>{docs}</p></div>
              </div>
            ))}
          </div>
        </div>
        <div className='RHS'>
          <div className='topic'>
            <p>{project.topic}</p>
          </div>
          <div className='rating'>
            <p>{project.rating}/5</p>
          </div>
        </div>
      </div>
      ))}

    </>
  );
}

export default ProjectList;
