import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAlignCenter, faCoffee, faFaceMehBlank } from '@fortawesome/free-solid-svg-icons';
import logo from '../../logo.svg'; // Adjust the path accordingly
import './Home.css';
import { useNavigate } from 'react-router-dom';

function Home() {
  const navigate = useNavigate();

  const [domainValue, setdomainValue] = useState('');
  const [TopicValue, setTopicValue] = useState('');
  const [options2, setOptions2] = useState([]);
  const [formSubmitted, setFormSubmitted] = useState(false);

  // Sample data for options
  const dynamicOptions1 = [
    { value: 'computerscience', label: 'Computer Science' },
    { value: 'mechanical', label: 'Mechanical' },
    { value: 'civil', label: 'Civil' },
  ];

  const dynamicOptions2 = {
    computerscience: [
      { value: 'webdev', label: 'Web Development' },
      { value: 'datascience', label: 'Data Science' },
    ],
    mechanical: [
      { value: 'automobiles', label: 'Automobiles' },
      { value: 'machines', label: 'Machines' },
    ],
    civil: [
      { value: 'buildings', label: 'Buildings' },
      { value: 'roads', label: 'Roads' },
    ],
  };

  useEffect(() => {
    // Update options of Dropdown 2 based on the selected value of Dropdown 1
    setOptions2(dynamicOptions2[domainValue] || []);
    // Reset selected value of Dropdown 2
    setTopicValue('');
  }, [domainValue]);

  const handleOption1Change = (event) => {
    setdomainValue(event.target.value);
  };

  const handleOption2Change = (event) => {
    setTopicValue(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setFormSubmitted(true);
    if(domainValue=='')
    return;
    // Perform actions with selected options, e.g., submit to a server
    console.log('Selected Option 1:', domainValue);
    console.log('Selected Option 2:', TopicValue);
    setFormSubmitted(false);
    navigate(`/project-list?domain=${domainValue}&topic=${TopicValue === '' ? 'All' : TopicValue}`);
  };

  return (
    <>
      
      <div className='body-div'>
        <div>
          <h1>Welcome to Project Vault</h1>
          <p>Explore the perfect projects for your academic requirements, with all source codes, reports, included</p>
        </div>
        
        <form onSubmit={handleSubmit}>
          <div className='dropdown'>
            {/* <select className='dropdown-box' value={domainValue} onChange={handleOption1Change}> */}
            <select className={`dropdown-box${formSubmitted && domainValue === '' ? '-error' : ''}`} value={domainValue} onChange={handleOption1Change}>
              <option value=''>Select Domain</option>
              {dynamicOptions1.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>

          <div className='dropdown'>
            <select className='dropdown-box' value={TopicValue} onChange={handleOption2Change}>
              <option value=''>Select Topic</option>
              {options2.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>

          <button type='submit'>Search Projects</button>
        </form>
      </div>
    </>
  );
}

export default Home;
