import React, { useState, useEffect, useRef } from 'react';
import './ProjectList.css';
import { useLocation } from 'react-router-dom';
import Header from '../../Components/Header/Header';
import { useNavigate } from 'react-router-dom';
import { fetchProjectList } from '../../Service/api';

function ProjectList() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const queryParams = new URLSearchParams(search);
  const [selectedDomain, setselectedDomain] = useState(queryParams.get('domain'));
  const [selectedTopic, setselectedTopic] = useState(queryParams.get('topic'));

  const renovateParams = async () => {
    switch(selectedDomain)
    {
      case 'computerscience' :
        selectedDomain='Computer Science';
      break;
      case 'mechanical' :
        selectedDomain='Mechanical';
      break;
      case 'civil' :
        selectedDomain='Civil';
      break;
    }
    switch(selectedTopic)
    {
      case 'appdev' :
        selectedTopic='App Development';
      break;
      case 'machinelearning' :
        selectedTopic='Machine Learning';
      break;
      case 'blockchain' :
        selectedTopic='blockchain';
      break;
    }
  };

  const [projectList, setProjectList] = useState([]);
  const [loader, setLoader] = useState(true);

  const handleCallAPI = async () => {
    // const response = await fetchProjectList('Computer Science', 'All');
    const response = await fetchProjectList(selectedDomain,selectedTopic);
    setProjectList(response.projects);
    setLoader(false);
  };

  useEffect(() => {
    console.log('Updated Project List:', projectList);
  }, [projectList]);

  useEffect(() => {
    handleCallAPI();
  }, []); // The empty dependency array means this effect runs once when the component mounts


  const handleProjectClick = (projectId) => {
    // Navigate to the next page with the project ID as a query parameter
    navigate(`/project?projectId=${projectId}`);
  };

  return (
    <>
      <Header/>
      <div className='heading'>
        <h2>Here are your search results</h2>
        <div className='fields'>
          <div className='field-display'>
            <p>{selectedDomain}</p>
          </div>
          <div className='field-display'>
            <p>{selectedTopic}</p>
          </div>
        </div>
      </div>

      { !loader && projectList.length>0 && projectList.map((project) => (
        <div className='project-box' key={project._id} onClick={() => handleProjectClick(project._id)}>
        <div className='LHS'>
          <h3>{project.title}</h3>
          <p>{project.description}</p>
          <div className='av-docs'>
            {project.documents.map((docs) => (
              <div>
                <div className='doc'><p>{docs.key}</p></div>
              </div>
            ))}
          </div>
        </div>
        <div className='RHS'>
          <div className='topic'>
            <p>{project.domain}</p>
          </div>
          <div className='rating'>
            <p>{project.rating}/5</p>
          </div>
        </div>
      </div>
      ))}

    </>
  );
}

export default ProjectList;
